import mongoose from 'mongoose'; 
import config from './config'; 

(async () => { 
    try { 
        const db = await mongoose.connect(config.CONNECTION_STRING, { 
            //useNewUrlParser: true, 
            //useUnifiedTopology: true, 
            dbName: config.DATABASE 
