import * as mongoose from 'mongoose';

/**
 * NOTA 6.1
 * Esquema ProdServ para productos/servicios en MongoDB usando Mongoose.
 * Cada campo est√° dise√±ado para cubrir necesidades espec√≠ficas de negocio y trazabilidad.
 */

const prodservSchema = new mongoose.Schema({
    // Clave primaria √∫nica para el producto/servicio.s
    IdProdServPK: { type: Number, required: true }, // Permite b√∫squedas r√°pidas y evita duplicados.

    // Claves alternativas para integraci√≥n con otros sistemas o referencias externas.
    IdProdServOK: { type: String }, // Clave operativa.
    IdProdServBK: { type: String }, // Clave de negocio.

    //-- Claves de marca para asociar el producto/servicio con una marca espec√≠fica.
    IdProdServMaOK: { type: String }, // Clave operativa de marca.
    IdProdServMaBK: { type: String }, // Clave de negocio de marca.

    //-- Descripci√≥n textual del producto/servicio.
    DesProdServ: { type: String }, // Permite mostrar informaci√≥n legible al usuario.

    // Claves para la unidad de medida del producto/servicio.
    IdMedidaOK: { type: String }, // Clave operativa de medida.
    IdMedidaBK: { type: String }, // Clave de negocio de medida.

    // Historial de estatus del producto/servicio.
    cat_prod_serv_estatus: [
        {
            IdTipoGenEstatusOk: { type: String }, // Tipo de estatus (ej. activo, inactivo).
            IdGenEstatusOk: { type: String }, // Clave espec√≠fica del estatus.
            TipoEstatus: { type: String }, // Descripci√≥n del tipo de estatus.
            Actual: { type: String }, // Indica si es el estatus actual.
            Observacion: { type: String }, // Comentarios o notas sobre el estatus.
            detail_row: { // Trazabilidad de qui√©n y cu√°ndo se registr√≥ el estatus.
                FechaReg: { type: Date, default: Date.now }, // Fecha de registro.
                UsuarioReg: { type: String } // Usuario que registr√≥ el estatus.
            },
            _id: false, // Evita que Mongoose genere un _id para cada subdocumento de estatus.
        },
    ],

    // Archivos asociados al producto/servicio (im√°genes, documentos, etc.).
    cat_prod_serv_archivos: [
        {
            DesArchivo: { type: String }, // Descripci√≥n del archivo.
            RutaArchivo: { type: String }, // Ruta o URL del archivo.

            // Informaci√≥n sobre el tipo de archivo.
            IdTipoGenArchivoOK: { type: String }, // Clave operativa del tipo de archivo.
            IdGenArchivoOK: { type: String }, // Clave espec√≠fica del archivo.
            TipoArchivo: { type: String }, // Descripci√≥n del tipo de archivo.

            // Informaci√≥n sobre la secci√≥n a la que pertenece el archivo.
            IdTipoGenSeccionOK: { type: String }, // Clave operativa de la secci√≥n.
            IdGenSeccionOK: { type: String }, // Clave espec√≠fica de la secci√≥n.
            TipoSeccion: { type: String }, // Descripci√≥n de la secci√≥n.

            // Orden y relevancia del archivo.
            Secuencia: { type: Number }, // Permite ordenar los archivos.
            Principal: { type: String }, // Indica si el archivo es el principal.

            // Trazabilidad y control de cambios del archivo.
            detail_row: {
                FechaReg: { type: Date, default: Date.now }, // Fecha de registro.
                UsuarioReg: { type: String }, // Usuario que registr√≥ el archivo.
                FechaUltMod: { type: Date, default: Date.now }, // Fecha de √∫ltima modificaci√≥n.
                UsuarioMod: { type: String }, // Usuario que modific√≥ el archivo.
                Activo: { type: String, default: 'S' }, // Indica si el archivo est√° activo.
                Borrado: { type: String, default: 'N' }, // Indica si el archivo est√° borrado l√≥gicamente.
                _id: false, // Evita generaci√≥n de _id en el subdocumento de trazabilidad.
            },
            _id: false, // Evita generaci√≥n de _id en el subdocumento de archivo.
        },
    ],

    // Trazabilidad y control de cambios del producto/servicio.
    detail_row: {
        FechaReg: { type: Date, default: Date.now }, // Fecha de registro del producto/servicio.
        UsuarioReg: { type: String }, // Usuario que registr√≥ el producto/servicio.
        FechaUltMod: { type: Date, default: Date.now }, // Fecha de √∫ltima modificaci√≥n.
        UsuarioMod: { type: String }, // Usuario que modific√≥ el producto/servicio.
        Activo: { type: String, default: 'S' }, // Indica si el producto/servicio est√° activo.
        Borrado: { type: String, default: 'N' } // Indica si el producto/servicio est√° borrado l√≥gicamente.
    }
});

//Commerce

export default mongoose.model( 
  'cat_prod_serv', 
  prodservSchema, 
  'cat_prod_serv' 
);

