    const updatedDoc = await ProdServ.findOne(filterBase);
    return {
      success: true,
      arrayName,
      counts: { pushed, updated: setted },
      doc: updatedDoc,
    };
  } catch (error) {
    throw boom.badImplementation(error);
  }
